  {id: 1, title: "The Shawshank Redemption", year: 1994, duration: 142, description: "Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.", image_url: "http://ia.media-imdb.com/images/M/MV5BMTc3NjM4MTY3MV5BMl5BanBnXkFtZTcwODk4Mzg3OA@@._V1_SY317_CR4,0,214,317_.jpg", director_id: 1, created_at: "2015-03-04 05:07:03", updated_at: "2015-03-04 05:07:03"},
  {id: 2, title: "The Godfather", year: 1972, duration: 175, description: "The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.", image_url: "http://ia.media-imdb.com/images/M/MV5BMjEyMjcyNDI4MF5BMl5BanBnXkFtZTcwMDA5Mzg3OA@@._V1_SX214_.jpg", director_id: 2, created_at: "2015-03-04 05:07:03", updated_at: "2015-03-04 05:07:03"},
  {id: 3, title: "The Godfather: Part II", year: 1974, duration: 200, description: "The early life and career of Vito Corleone in 1920s New York is portrayed while his son, Michael, expands and tightens his grip on his crime syndicate stretching from Lake Tahoe, Nevada to pre-revolution 1958 Cuba.", image_url: "http://ia.media-imdb.com/images/M/MV5BNDc2NTM3MzU1Nl5BMl5BanBnXkFtZTcwMTA5Mzg3OA@@._V1_SX214_.jpg", director_id: 2, created_at: "2015-03-04 05:07:03", updated_at: "2015-03-04 05:07:03"},
